package launcher;

import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int op;
		Scanner in = new Scanner(System.in);
		
		
		do {
			op = in.nextInt();
			switch(op) {
				case 1: //function calls
					break;
				case 2: //...
					break;
				//...
			}
		}while(op != 11);

		in.close();
	}

}
